import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const Project = () => {
    const [td, setTd] = useState(null);
    const [pdetails, setPdetails] = useState(null);
    const location = useLocation();
    useEffect(() => {
        setPdetails(location.state);
      }, []);

      useEffect(() => {
        const fetchData = async () => {
        if(once){
            const d = {
                'id': pdetails[0]._id.$oid
            }
            fetch('/assignments',{
                'method':'POST',
                 headers : {
                'Content-Type':'application/json'
          },
          body:JSON.stringify(d)
        })
        .then(async (response) =>{
          const data = await response.json();
          setTd(data);
        })
        .catch(error => console.log(error))
        }
    }
    fetchData();
      }, []);
    
      useEffect(() => {
        console.log(td);
      }, [td]);
    return(
        <div>
            Project Info
            {
                (pdetails != null)?
                <div>
                {pdetails.map((item, index) => (
                                        <div className="project_data_teacher">
                                            <div>Project ID: {item._id.$oid}</div>
                                            <div>Project Name: {item.project_name}</div>
                                        </div>
                                    ))}
                </div>:
                <div></div>
            }
            {
                (t)
            }
        </div>
    )
}

export default Project;
